    int Partida(int TeamA, int TeamB){
        if (Forca[TeamA] > Forca[TeamB]){ return TeamA; }
        else if (Forca[TeamA] < Forca[TeamB]) { return TeamB; }
        else {
            Random nrd = new Random();
            var result = nrd.Next(2);
            if (result == 0) { return TeamA; }
            else { return TeamB; }
        }
    }
